import React from 'react';
import './AdoptionPage.css';
import ApiService from '../ApiService';
import baseUrl from '../config';


class AdoptionPage extends React.Component{

    state = {
        cats: [],
        dogs: [],
        people: [],
        name: ''
    };


componentDidMount(){
    this.setFirstCat()
    this.setFirstDog()
    this.setAllPeople()
}

setFirstCat = () => {
    ApiService.getCat()
    .then(res => {
        this.setState({
            cats: res
        })
    })
    .catch({ error: 'An error occurred.'})
}

setFirstDog = () => {
    ApiService.getDog()
    .then(res => {
        this.setState({
            dogs: res
        })
    })
    .catch({ error: 'An error occurred.'})
}

setAllPeople = () => {
    ApiService.getPeople()
    .then(res => {
        this.setState({
            people: res
        })
    })
    .catch({ error: 'An error occurred.'})
}

// create settimeout function with adopt cat and dog


adoptCat = () => {
    ApiService.adoptCat()
    .then(() => {
        this.setState({
            cats: this.state.cats.splice(1)
        })
    })
}

adoptDog = () => {
    ApiService.adoptDog()
    .then(() => {
        this.setState({
            dogs: this.state.dogs.splice(1)
        })
    })
}

// This function calls addPerson function (post request) and adds name to list of people array
 handleAddPerson = (e) => {
     e.preventDefault();
     ApiService.addPerson(this.state.name)
     .then(() => {
       this.setState({
           people: [...this.state.people, this.state.name]
       })
     })
 }

 // Handles on change event when adding name
 handleOnChange = (e) => {
     e.preventDefault();
     this.setState({
        name: e.target.value
    }) 
 }


 renderCatsAndDogs(){
     this.renderPet(this.state.dogs[0], 'Dog')
     let catName = this.state.cats.name;
     console.log(catName, 'CATNAME')
     let catPic = this.cats.imageURL;
     let catDescription = this.cats.description;
     let catGender = this.cats.gender;
     let catAge = this.cats.age;
     let catBreed = this.cats.breed;
     let catStory = this.cats.story;


     

     return(
         <div>
         <img src={catPic} alt='Cat'></img>
         <fieldset>
             <h3>{catName}</h3>
             <p>Description: {catDescription}</p>
             <p>Name: {catName}</p>
             <p>Age: {catAge}</p>
             <p>Gender: {catGender}</p>
             <p>Breed: {catBreed}</p>
             <p>Story: {catStory}</p>
         </fieldset>

         <img src={dogPic} alt='Dog'></img>
         <fieldset>
             <h3>{dogName}</h3>
             <p>Description: {dogDescription}</p>
             <p>Name: {dogName}</p>
             <p>Age: {dogAge}</p>
             <p>Gender: {dogGender}</p>
             <p>Breed: {dogBreed}</p>
             <p>Story: {dogStory}</p>
         </fieldset>
         </div>
     )
 };

 renderPet(petObj, petType){
    let petName = petObj.name;
    let petPic = petObj.imageURL;
    let petDescription = petObj.description;
    let petGender = petObj.gender;
    let petAge = petObj.age;
    let petBreed = petObj.breed;
    let petStory = petObj.story;

     return(
         <div>
        <img src={dogPic} alt={petType}></img>
        <fieldset>
            <h3>{dogName}</h3>
            <p>Description: {dogDescription}</p>
            <p>Name: {dogName}</p>
            <p>Age: {dogAge}</p>
            <p>Gender: {dogGender}</p>
            <p>Breed: {dogBreed}</p>
            <p>Story: {dogStory}</p>
        </fieldset>
        </div>
     )
 }

    render(){
        return(
            <div>
                <ul>
                    {this.state.people.map(person => {return <li>{person}</li>})}
                </ul>

                <div>
                    {this.renderCatsAndDogs()};
                </div>

                <fieldset>
                    <h3>Add Name to List: </h3>
                    <form onSubmit={this.handleAddPerson}>
                    <label htmlFor='fullName'>Name: </label>
                    <input type='text' name='fullName' value={this.state.name} onChange={this.handleOnChange} placeholder='Enter your name'  required></input>
                    <button type='submit'>Add Name</button>
                    </form>
                </fieldset>
            </div>
        )
    }
}

export default AdoptionPage;
